import React from "react";
import { useState } from "react";
import "../style.css"
import { 
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBRow,
    MDBCol
 } from "mdb-react-ui-kit";
 import { ProfileCard } from "./card";




export function TableSection(props) {

    const {tableTitle, data} = props

    function allowDrop(ev) {
        ev.preventDefault();
      }

      function drag(ev) {
        ev.dataTransfer.setData("text", ev.target.id);
      }

      function drop(ev) {
        ev.preventDefault();
        var data = ev.dataTransfer.getData("text");
        ev.target.appendChild(document.getElementById(data));
      }



    return (
        <MDBCard className="table-section" id="dropTarget" ondrop="drop(event)" ondragover="allowDrop(event)">
            <MDBCardHeader className="tableTitle" >
                {tableTitle}
            </MDBCardHeader>
            <MDBCardBody>
                <MDBRow className="tableSection-body h-60">
                    <MDBCol>
                        {
                          data
                        }                     
                    </MDBCol>
                </MDBRow>
            </MDBCardBody>
        </MDBCard>
    )



}
