import React from "react";
import { useState } from "react";
import "../style.css"
import { 
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBRow,
    MDBCol
 } from "mdb-react-ui-kit";
 import { ProfileCard } from "./card";




export function TableSection(props) {

    const {tableTitle, data} = props
    const [draggedElementId, setDraggedElementId] = useState(null);


    function handleDragOver(e){
        e.preventDefault();
    }

    const handleDrop = (event) => {
        event.preventDefault();

        if (!draggedElementId) return;

        const draggedElement = document.getElementById(draggedElementId);
        const targetElement = event.target;

        const maxX = window.innerWidth - draggedElement.offsetWidth;
        const maxY = window.innerHeight - draggedElement.offsetHeight;
        const randomX = Math.floor(Math.random() * maxX);
        const randomY = Math.floor(Math.random() * maxY);

        const clonedElement = draggedElement.cloneNode(true);

        clonedElement.style.position = 'absolute';
        clonedElement.style.left = `${randomX}px`;
        clonedElement.style.top = `${randomY}px`;

        targetElement.appendChild(clonedElement);

        setDraggedElementId(null);
      };

    return (
        <MDBCard className="table-section" onDrop={handleDrop} onDragOver={handleDragOver} id="dropTarget">
            <MDBCardHeader className="tableTitle" >
                {tableTitle}
            </MDBCardHeader>
            <MDBCardBody>
                <MDBRow className="tableSection-body h-60">
                    <MDBCol>
                        {
                          data
                        }                     
                    </MDBCol>
                </MDBRow>
            </MDBCardBody>
        </MDBCard>
    )



}
